import React, { Component } from 'react'
import NewsItems from './NewsItems'

export class News extends Component {
    constructor(){
        super();
        this.state ={
            articles: this.articles,
            loading: false
        }
    }
    async componentDidMount(){
        let url = "https://newsapi.org/v2/top-headlines?country=in&apiKey=62c991ede9f94b27862a55ee3e98eabe";
        let data = await fetch(url);
        let parsedData =  await data.json()
        console.log(data);
        this.setState({articles:parsedData.articles})
    }
    render() {
        return (
            <div className='container my-3'>
                <h2>NewsMonkey - Top Headlines</h2>
                <div className="row">
                {this.state.articles.map((element)=>{
                    return <div className="col-md-4" key={element.url}>
                    <NewsItems title={element.title?element.title:""} description={element.description?element.description:""} imageUrl ={element.urlToImage} newsUrl={element.url}/>
            </div>
            

                })}
                  </div>  
            </div>
        )
    }
}

export default News
