import React, { Component } from 'react'
import NewsItems from './NewsItems'

export class News extends Component {
    constructor(){
        super();
        this.state ={
            articles: [],
            loading: false,
            page:1,

        }
    }
    async componentDidMount(){
        let url = "https://newsapi.org/v2/top-headlines?country=us&apiKey=62c991ede9f94b27862a55ee3e98eabe&page=1&pageSize=20";
        let data = await fetch(url);
        let parsedData =  await data.json()
        console.log(data);
        this.setState({articles:parsedData.articles, totalResults:parsedData.totalResults})
    }
     handlePrevClick = async() =>{
        let url = `https://newsapi.org/v2/top-headlines?country=us&apiKey=62c991ede9f94b27862a55ee3e98eabe&page=${this.state.page - 1}&pageSize=20`;
        let data = await fetch(url);
        let parsedData =  await data.json()
        console.log(parsedData);

        this.setState({
            page: this.state.page-11,
            articles:parsedData.articles
        })
    }
    handleNextClick =async () =>{
        if(this.state.page+1 > Math.ceil(this.state.totalResults/20)){
            
        }
        else{
            let url = `https://newsapi.org/v2/top-headlines?country=us&apiKey=62c991ede9f94b27862a55ee3e98eabe&page=${this.state.page + 1}&pageSize=20`;
        let data = await fetch(url);
        let parsedData =  await data.json()
        console.log(parsedData);

        this.setState({
            page: this.state.page+1,
            articles:parsedData.articles
        })
        }
    }
    render() {
        return (
            <div className='container my-3'>
                <h1 className='header-container text-center'>NewsMonkey - Top Headlines</h1>
                <div className="row">
                {this.state.articles.map(()=>{
                    return <div className="col-md-4" key={element.url}>
                    <NewsItems title={element.title?element.title:""} description={element.description?element.description:""} imageUrl ={element.urlToImage} newsUrl={element.url}/>
            </div>
            

                })}
                  </div>  
                  <div className="container d-flex justify-content-between">
                  <button disabled={this.state.page<=1} type="button" className=" prevnexbtn btn btn-info" onClick={this.handlePrevClick}>&laquo; Previous</button>
                  <button disabled={this.state.page+1 > Math.ceil(this.state.totalResults/20)} type="button" className="prevnexbtn btn btn-info"onClick={this.handleNextClick} > Next &raquo;</button>
                  </div>
            </div>
        )
    }
}

export default News
