import React from 'react'
export default function TextForm(props) {
  return (
    <div>
        <div class="mb-3">
        <label for="myBox" class="form-label">{props.heading}</label>
        <textarea class="form-control" id="myBox" rows="3"></textarea>
        </div>
    </div>
  )
}
